import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import "./todo-list-item.css"
export default class TodoListItem extends Component {

  state = {
    done: false,
    important:false
  }

  onLabelClick = () => {
    this.setState(({done})=>{
      return{
      done: !done
    }
    })
  };
  onMarkImportant=()=>{
    this.setState(({important})=>{
      return{
      important: !important
    } 
    })
  }

  render() {
    const { label,onDeleted ,onToggleDone,onToggleImportant } = this.props;
    const { done, important } = this.state;
    let classNames = 'todo-list-item';
    if (done) {
      classNames += ' done';
    }
    if(important){
      classNames += ' important';
    }



    return (
      <span className={classNames}>
        <span onClick={onToggleDone}
          className="todo-list-item-label"
          >
          {label}
        </span>
        <span>
          <button type="button" onClick={onToggleDone} className="btn btn-outline-success btn-sm float-right">
            <i className="fas fa-exclamation" ></i>
          </button>

          <button type="button" onClick={onDeleted} className="btn btn-outline-danger btn-sm float-right">
            <i className="fas fa-trash-alt"></i>
          </button>
        </span>
      </span>
    );
  };
}
